import { Component } from '@angular/core';
import { Product } from '../product';
import { ProductService } from '../product.service';

@Component({
  selector: 'app-product',
  templateUrl: './product.component.html',
  styleUrls: ['./product.component.css']
})
export class ProductComponent {
  // products: any[]; // Define the type of 'products' as needed

  // constructor(private productService: ProductService) {
  //   this.products = []; // Initialize 'products' with an empty array or an appropriate initial value
  // }
  products: Product[] = [];

  constructor(private productService: ProductService) {}

  ngOnInit(): void {
    this.getProducts();

  }

  private getProducts(){
    this.productService.getProductsList().subscribe(data => {this.products=data;});
  }

}
<h2>Product List</h2>
<table class="simple-table">
    <thead>
        <tr>
            <th>Details</th>
            <th>Internal Name</th>
            <th>Max Prod Per Loc</th>
            <th>Name</th>
            <th>Actions</th>
        </tr>
    </thead>
    <tbody>
        <tr *ngFor="let product of products">
            <td>{{ product.details }}</td>
            <td>{{ product.internal_name }}</td>
            <td>{{ product.maxprodperloc }}</td>
            <td>{{ product.name }}</td>
            <td>
                <!-- <button (click)="productDetails(product.id)" class="view-button">View</button> -->
            </td>
        </tr>
    </tbody>
</table>
